// Enhanced Login Component
function Login({ onLogin }) {
  const [email, setEmail] = useState('admin@example.com');
  const [password, setPassword] = useState('password');
  const [err, setErr] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const submit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const res = await API.post('/auth/login', { email, password });
      const { token, user } = res.data;
      localStorage.setItem('token', token);
      onLogin(user);
    } catch (err) {
      console.error(err);
      setErr(err?.response?.data?.error || 'Login failed');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center" style={{ backgroundColor: colors.background }}>
      <div className="col-md-5">
        <div className="card shadow-lg border-0">
          <div className="card-body p-5">
            <div className="text-center mb-4">
              <div className="d-flex justify-content-center align-items-center mb-3">
                <FaBox className="me-2" style={{ color: colors.primary, fontSize: '2rem' }} />
                <h1 className="fw-bold" style={{ color: colors.primary }}>InventoryApp</h1>
              </div>
              <p className="text-muted">Sign in to your account</p>
            </div>

            {err && <div className="alert alert-danger">{err}</div>}

            <form onSubmit={submit}>
              <div className="mb-3">
                <label className="form-label">Email address</label>
                <div className="input-group">
                  <span className="input-group-text"><FaUser /></span>
                  <input 
                    className="form-control" 
                    value={email} 
                    onChange={e => setEmail(e.target.value)} 
                    placeholder="Email" 
                    required
                  />
                </div>
              </div>

              <div className="mb-4">
                <label className="form-label">Password</label>
                <div className="input-group">
                  <span className="input-group-text"><FaCog /></span>
                  <input 
                    className="form-control" 
                    value={password} 
                    onChange={e => setPassword(e.target.value)} 
                    placeholder="Password" 
                    type="password" 
                    required
                  />
                </div>
              </div>

              <button 
                className="btn btn-primary w-100 py-2" 
                type="submit" 
                disabled={isLoading}
                style={{ backgroundColor: colors.primary, borderColor: colors.primary }}
              >
                {isLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Signing in...
                  </>
                ) : 'Sign In'}
              </button>
            </form>

            <div className="mt-4 text-center text-muted small">
              Use register endpoint to create users (for dev purposes).
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Main App Component
export default function App() {
  const [user, setUser] = useState(null);
  const [view, setView] = useState('dashboard');
  const [isLoading, setIsLoading] = useState(true);
  const [authError, setAuthError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoading(false);
      return;
    }

    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setUser({ name: payload.email, role: payload.role });
    } catch(e) {
      console.error(e);
      setAuthError('Invalid authentication token');
      localStorage.removeItem('token');
    } finally {
      setIsLoading(false);
    }
  }, []);

  const handleLogout = () => { 
    localStorage.removeItem('token'); 
    setUser(null); 
  };

  if (isLoading) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center" style={{ backgroundColor: colors.background }}>
        <LoadingSpinner />
      </div>
    );
  }

  if (authError) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center" style={{ backgroundColor: colors.background }}>
        <div className="col-md-5">
          <div className="card shadow-lg border-0">
            <div className="card-body p-5">
              <div className="text-center mb-4">
                <h3 className="text-danger">Authentication Error</h3>
                <p>{authError}</p>
                <button 
                  className="btn btn-primary" 
                  onClick={() => window.location.href = '/'}
                  style={{ backgroundColor: colors.primary }}
                >
                  Go to Login
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (!localStorage.getItem('token') || !user) {
    return <Login onLogin={(u) => setUser(u)} />;
  }

  return (
    <div className="d-flex flex-column min-vh-100" style={{ backgroundColor: colors.background }}>
      <Navbar onLogout={handleLogout} user={user} />

      <div className="d-flex flex-grow-1">
        <Sidebar onNav={setView} activeView={view} />

        <div className="flex-grow-1 p-4 overflow-auto">
          {view === 'dashboard' && <Dashboard />}
          {view === 'products' && <Products />}
          {view === 'purchases' && <Purchases />}
          {view === 'sales' && <Sales />}
          {view === 'reports' && <Reports />}
        </div>
      </div>
    </div>
  );
}

// Enhanced Dashboard
function Dashboard() {
