// Enhanced Products Component
function Products() {
  const [products, setProducts] = useState([]);
  const [q, setQ] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [error, setError] = useState(null);

  const fetch = async () => {
    setIsLoading(true);
    try {
      const res = await API.get('/products', { params: { q } });
      setProducts(res.data);
      setError(null);
    } catch (err) {
      console.error('Failed to load products', err);
      setError('Failed to load products. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetch();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await API.delete('/products/' + id);
        fetch();
      } catch (err) {
        console.error('Failed to delete product', err);
        setError('Failed to delete product. Please try again.');
      }
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
    setShowAddForm(true);
  };

  const handleFormSaved = () => {
    fetch();
    setShowAddForm(false);
    setEditingProduct(null);
  };

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">Products</h2>
        <button 
          className="btn btn-primary d-flex align-items-center" 
          onClick={() => setShowAddForm(!showAddForm)}
          style={{ backgroundColor: colors.primary }}
        >
          <FaPlus className="me-2" /> {showAddForm ? 'Cancel' : 'Add Product'}
        </button>
      </div>

      {error && <ErrorComponent message={error} onRetry={fetch} />}

      {showAddForm && (
        <ProductForm 
          onSaved={handleFormSaved} 
          product={editingProduct}
          onCancel={() => {
            setShowAddForm(false);
            setEditingProduct(null);
          }}
        />
      )}

      <div className="card border-0 shadow-sm">
        <div className="card-body">
          <div className="d-flex mb-3">
            <div className="input-group me-2" style={{ maxWidth: '300px' }}>
              <span className="input-group-text"><FaSearch /></span>
              <input 
                className="form-control" 
                value={q} 
                onChange={e => setQ(e.target.value)} 
                placeholder="Search products..." 
              />
            </div>
            <button className="btn btn-outline-primary" onClick={fetch}>
              <FaSearch />
            </button>
          </div>

          {isLoading ? (
            <LoadingSpinner />
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>SKU</th>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Supplier</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {products.length > 0 ? (
                    products.map(p => (
                      <tr key={p.id}>
                        <td>{p.sku}</td>
                        <td className="fw-bold">{p.name}</td>
                        <td>{p.category}</td>
                        <td>
                          <span className={`badge ${p.quantity < p.min_stock ? 'bg-danger' : 'bg-success'}`}>
                            {p.quantity}
                          </span>
                        </td>
                        <td>pkr{Number(p.price).toFixed(2)}</td>
                        <td>{p.supplier}</td>
                        <td>
                          <button 
                            className="btn btn-sm btn-outline-primary me-1" 
                            onClick={() => handleEdit(p)}
                          >
                            <FaEdit />
                          </button>
                          <button 
                            className="btn btn-sm btn-outline-danger" 
                            onClick={() => handleDelete(p.id)}
                          >
                            <FaTrash />
                          </button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" className="text-center py-4">
                        No products found
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          )}

          <div className="d-flex justify-content-between align-items-center mt-3">
            <div className="text-muted">
              Showing {products.length} products
            </div>
            <button className="btn btn-outline-primary" onClick={() => { window.location = '/api/products/export/csv'; }}>
              <FaDownload className="me-2" /> Export CSV
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

// Enhanced Product Form
function ProductForm({ onSaved, product, onCancel }) {
  const [form, setForm] = useState({
    name: '',
    sku: '',
    category: '',
    price: 0,
    quantity: 0,
    supplier: '',
    min_stock: 5
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (product) {
      setForm(product);
    }
  }, [product]);

  const submit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      if (product) {
        await API.put('/products/' + product.id, form);
      } else {
        await API.post('/products', form);
      }
      onSaved();
      setError(null);
    } catch (err) {
      console.error(err);
      setError('Failed to save product. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="card border-0 shadow-sm mb-4">
      <div className="card-body">
        <h5 className="card-title mb-4">{product ? 'Edit Product' : 'Add New Product'}</h5>

        {error && <ErrorComponent message={error} />}

        <form onSubmit={submit}>
          <div className="row g-3">
            <div className="col-md-6">
              <label className="form-label">Product Name</label>
              <input 
                className="form-control" 
                value={form.name} 
                onChange={e => setForm({...form, name: e.target.value})} 
                placeholder="Enter product name" 
                required
              />
            </div>

            <div className="col-md-6">
              <label className="form-label">SKU</label>
              <input 
                className="form-control" 
                value={form.sku} 
                onChange={e => setForm({...form, sku: e.target.value})} 
                placeholder="Enter SKU" 
                required
              />
            </div>

            <div className="col-md-6">
              <label className="form-label">Category</label>
              <input 
                className="form-control" 
                value={form.category} 
                onChange={e => setForm({...form, category: e.target.value})} 
                placeholder="Enter category" 
              />
            </div>

            <div className="col-md-6">
              <label className="form-label">Supplier</label>
              <input 
                className="form-control" 
                value={form.supplier} 
                onChange={e => setForm({...form, supplier: e.target.value})} 
                placeholder="Enter supplier" 
              />
            </div>

            <div className="col-md-4">
              <label className="form-label">Price (pkr)</label>
              <input 
                className="form-control" 
                value={form.price} 
                onChange={e => setForm({...form, price: e.target.value})} 
                placeholder="0.00" 
                type="number" 
                step="0.01" 
                min="0"
                required
              />
            </div>

            <div className="col-md-4">
              <label className="form-label">Quantity</label>
              <input 
                className="form-control" 
                value={form.quantity} 
                onChange={e => setForm({...form, quantity: e.target.value})} 
                placeholder="0" 
                type="number" 
                min="0"
                required
              />
            </div>

            <div className="col-md-4">
              <label className="form-label">Min Stock Alert</label>
              <input 
                className="form-control" 
                value={form.min_stock} 
                onChange={e => setForm({...form, min_stock: e.target.value})} 
                placeholder="5" 
                type="number" 
                min="0"
              />
            </div>

            <div className="col-12 d-flex justify-content-end">
              <button type="button" className="btn btn-outline-secondary me-2" onClick={onCancel}>
                Cancel
              </button>
              <button 
                className="btn btn-primary" 
                type="submit" 
                disabled={isLoading}
                style={{ backgroundColor: colors.primary }}
              >
                {isLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Saving...
                  </>
                ) : (product ? 'Update Product' : 'Add Product')}
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

